import type { VercelRequest, VercelResponse } from '@vercel/node'
import { ImageResponse } from '@vercel/og'
import React from 'react'

export default async function handler(req: VercelRequest, res: VercelResponse) {
  const { title, description, type, date, tags, format } = req.query

  if (!title) {
    return res.status(400).json({ error: 'Title is required' })
  }

  // Use React-based OG image if format is 'react' or not specified
  if (format !== 'html') {
    try {
      const imageResponse = new ImageResponse(
        React.createElement('div', {
          style: {
            height: '100%',
            width: '100%',
            display: 'flex',
            backgroundColor: '#ffffff',
            fontFamily: 'Inter, -apple-system, BlinkMacSystemFont, sans-serif',
            position: 'relative',
          }
        }, [
          // Background Pattern
          React.createElement('div', {
            key: 'bg-pattern',
            style: {
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              background: 'linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%)',
              opacity: 0.8,
            }
          }),
          
          // Main Content Container
          React.createElement('div', {
            key: 'main-container',
            style: {
              display: 'flex',
              width: '100%',
              height: '100%',
              padding: '80px',
              alignItems: 'center',
              justifyContent: 'space-between',
              position: 'relative',
              zIndex: 1,
            }
          }, [
            // Left Content
            React.createElement('div', {
              key: 'left-content',
              style: {
                display: 'flex',
                flexDirection: 'column',
                maxWidth: '700px',
                gap: '24px',
              }
            }, [
              // Type Badge
              React.createElement('div', {
                key: 'type-badge',
                style: {
                  display: 'inline-block',
                  backgroundColor: '#8b5cf6',
                  color: 'white',
                  padding: '8px 20px',
                  borderRadius: '25px',
                  fontSize: '16px',
                  fontWeight: 600,
                  textTransform: 'uppercase',
                  letterSpacing: '1px',
                  width: 'fit-content',
                }
              }, type === 'project' ? 'Project' : 'Article'),

              // Title
              React.createElement('h1', {
                key: 'title',
                style: {
                  fontSize: '64px',
                  fontWeight: 800,
                  lineHeight: 1.1,
                  color: '#1e293b',
                  margin: 0,
                  background: 'linear-gradient(135deg, #1e293b 0%, #8b5cf6 100%)',
                  backgroundClip: 'text',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                }
              }, title),

              // Description
              description && React.createElement('p', {
                key: 'description',
                style: {
                  fontSize: '24px',
                  lineHeight: 1.4,
                  color: '#64748b',
                  margin: 0,
                  fontWeight: 400,
                }
              }, description),

              // Author Info
              React.createElement('div', {
                key: 'author-info',
                style: {
                  display: 'flex',
                  alignItems: 'center',
                  gap: '16px',
                  marginTop: '24px',
                }
              }, [
                React.createElement('div', {
                  key: 'author-avatar',
                  style: {
                    width: '50px',
                    height: '50px',
                    borderRadius: '50%',
                    background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '20px',
                    fontWeight: 700,
                    color: 'white',
                  }
                }, 'HS'),
                
                React.createElement('div', {
                  key: 'author-details',
                  style: {
                    display: 'flex',
                    flexDirection: 'column',
                    gap: '2px',
                  }
                }, [
                  React.createElement('div', {
                    key: 'author-name',
                    style: {
                      fontSize: '18px',
                      fontWeight: 600,
                      color: '#1e293b',
                    }
                  }, 'Himanshu Salunke'),
                  
                  React.createElement('div', {
                    key: 'author-title',
                    style: {
                      fontSize: '14px',
                      color: '#64748b',
                    }
                  }, 'Aspiring Data Scientist')
                ])
              ])
            ])
          ])
        ]),
        {
          width: 1200,
          height: 630,
        }
      )

      return imageResponse
    } catch (e: any) {
      console.log(`${e.message}`)
      // Fall back to HTML version
    }
  }

  // Generate OG image HTML
  const ogImageHtml = `
    <!DOCTYPE html>
    <html>
      <head>
        <meta charset="utf-8">
        <style>
          * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
          }
          
          body {
            width: 1200px;
            height: 630px;
            font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            display: flex;
            align-items: center;
            justify-content: center;
            color: white;
            overflow: hidden;
          }
          
          .container {
            width: 100%;
            height: 100%;
            padding: 60px;
            display: flex;
            flex-direction: column;
            justify-content: center;
            position: relative;
          }
          
          .background-pattern {
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            opacity: 0.1;
            background-image: 
              radial-gradient(circle at 25% 25%, white 2px, transparent 2px),
              radial-gradient(circle at 75% 75%, white 2px, transparent 2px);
            background-size: 60px 60px;
          }
          
          .content {
            position: relative;
            z-index: 1;
            max-width: 900px;
          }
          
          .type-badge {
            display: inline-block;
            background: rgba(255, 255, 255, 0.2);
            padding: 8px 16px;
            border-radius: 20px;
            font-size: 14px;
            font-weight: 600;
            text-transform: uppercase;
            letter-spacing: 0.5px;
            margin-bottom: 24px;
            backdrop-filter: blur(10px);
          }
          
          .title {
            font-size: 64px;
            font-weight: 800;
            line-height: 1.1;
            margin-bottom: 24px;
            text-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
          }
          
          .description {
            font-size: 24px;
            line-height: 1.4;
            opacity: 0.9;
            margin-bottom: 32px;
            font-weight: 400;
          }
          
          .meta {
            display: flex;
            align-items: center;
            gap: 24px;
            font-size: 18px;
            opacity: 0.8;
          }
          
          .date {
            display: flex;
            align-items: center;
            gap: 8px;
          }
          
          .tags {
            display: flex;
            flex-wrap: wrap;
            gap: 12px;
            margin-top: 24px;
          }
          
          .tag {
            background: rgba(255, 255, 255, 0.15);
            padding: 6px 12px;
            border-radius: 12px;
            font-size: 14px;
            font-weight: 500;
            backdrop-filter: blur(10px);
          }
          
          .logo {
            position: absolute;
            bottom: 60px;
            right: 60px;
            font-size: 24px;
            font-weight: 700;
            opacity: 0.7;
          }
        </style>
      </head>
      <body>
        <div class="container">
          <div class="background-pattern"></div>
          <div class="content">
            <div class="type-badge">${type === 'project' ? 'Project' : 'Article'}</div>
            <h1 class="title">${title}</h1>
            ${description ? `<p class="description">${description}</p>` : ''}
            <div class="meta">
              ${date ? `<div class="date">ðŸ“… ${new Date(date as string).toLocaleDateString()}</div>` : ''}
            </div>
            ${tags ? `
              <div class="tags">
                ${(tags as string).split(',').slice(0, 5).map(tag => 
                  `<span class="tag">${tag.trim()}</span>`
                ).join('')}
              </div>
            ` : ''}
          </div>
          <div class="logo">Himanshu's Portfolio</div>
        </div>
      </body>
    </html>
  `

  // Set headers for image response
  res.setHeader('Content-Type', 'text/html')
  res.setHeader('Cache-Control', 'public, max-age=31536000, immutable')
  
  return res.status(200).send(ogImageHtml)
}

import { VercelRequest, VercelResponse } from '@vercel/node'
import { readdirSync, readFileSync } from 'fs'
import { join } from 'path'
import matter from 'gray-matter'

export default async function handler(req: VercelRequest, res: VercelResponse) {
  try {
    const projectsDir = join(process.cwd(), 'src/data/projects')
    const files = readdirSync(projectsDir).filter(file => file.endsWith('.mdx'))
    
    const projects = files
      .map(file => {
        const filePath = join(projectsDir, file)
        const fileContent = readFileSync(filePath, 'utf8')
        
        // Filter out commented/hidden projects
        if (fileContent.trim().startsWith('<!--')) {
          return null
        }
        
        const { data: frontmatter, content } = matter(fileContent)
        
        return {
          ...frontmatter,
          slug: frontmatter.id,
          content,
          readingTime: Math.ceil(content.split(' ').length / 200)
        }
      })
      .filter((project): project is NonNullable<typeof project> => project !== null)
    
    // Filter featured projects and sort by date
    const featuredProjects = projects
      .filter((project: any) => project.featured === true)
      .sort((a: any, b: any) => {
        const dateA = new Date(a.date || '2024-01-01')
        const dateB = new Date(b.date || '2024-01-01')
        return dateB.getTime() - dateA.getTime()
      })
    
    res.setHeader('Cache-Control', 'public, max-age=3600')
    res.status(200).json(featuredProjects)
  } catch (error) {
    console.error('Error fetching featured projects:', error)
    res.status(500).json({ error: 'Failed to fetch featured projects' })
  }
}

import { VercelRequest, VercelResponse } from '@vercel/node'
import { readdirSync, readFileSync } from 'fs'
import { join } from 'path'
import matter from 'gray-matter'




export default async function handler(req: VercelRequest, res: VercelResponse) {
  // Original projects API functionality
  try {
    const { featured, slug } = req.query

    const projectsDir = join(process.cwd(), 'src/data/projects')
    const files = readdirSync(projectsDir).filter(file => file.endsWith('.mdx'))

    const projects = files
      .map(file => {
      const filePath = join(projectsDir, file)
      const fileContent = readFileSync(filePath, 'utf8')

        // Filter out commented/hidden projects
        // Projects are hidden by wrapping frontmatter in HTML comments
        const trimmedContent = fileContent.trim()
        if (trimmedContent.startsWith('<!--')) {
          console.log(`[API] Filtering out commented project: ${file}`)
          return null
        }

        // Try to parse frontmatter - if it fails or is empty, skip
        let frontmatter, content
        try {
          const parsed = matter(fileContent)
          frontmatter = parsed.data
          content = parsed.content
        } catch (error) {
          console.log(`[API] Error parsing frontmatter for ${file}:`, error)
          return null
        }

        // Additional safety check: if frontmatter is missing required fields, skip
        if (!frontmatter || !frontmatter.id || !frontmatter.title) {
          console.log(`[API] Filtering out project with missing frontmatter: ${file}`)
          return null
        }

        // Double-check: if the file content suggests it's commented, skip
        if (fileContent.includes('COMMENTED OUT PROJECT') || fileContent.includes('COMMENTED OUT -')) {
          console.log(`[API] Filtering out project marked as commented: ${file}`)
          return null
        }

      return {
        ...frontmatter,
        slug: frontmatter.id,
        content,
        readingTime: Math.ceil(content.split(' ').length / 200)
      }
    })
      .filter((project): project is NonNullable<typeof project> => project !== null)

    console.log(`[API] Total projects after filtering: ${projects.length}`)

    // Handle specific project by slug
    if (slug && typeof slug === 'string') {
      const project = projects.find(p => p.slug === slug)
      if (!project) {
        return res.status(404).json({ error: 'Project not found' })
      }
      res.setHeader('Cache-Control', 'public, max-age=3600')
      return res.status(200).json(project)
    }

    // Handle featured projects filter
    if (featured === 'true') {
      const featuredProjects = projects
        .filter((project: any) => project.featured === true)
        .sort((a: any, b: any) => {
          const dateA = new Date(a.date || '2024-01-01')
          const dateB = new Date(b.date || '2024-01-01')
          return dateB.getTime() - dateA.getTime()
        })

      res.setHeader('Cache-Control', 'public, max-age=3600')
      return res.status(200).json(featuredProjects)
    }

    // Return all projects sorted by date
    const sortedProjects = projects.sort((a: any, b: any) => {
      const dateA = new Date(a.date || '2024-01-01')
      const dateB = new Date(b.date || '2024-01-01')
      return dateB.getTime() - dateA.getTime()
    })

    // Disable cache temporarily to ensure fresh data
    res.setHeader('Cache-Control', 'no-cache, no-store, must-revalidate')
    res.setHeader('Pragma', 'no-cache')
    res.setHeader('Expires', '0')
    res.status(200).json(sortedProjects)
  } catch (error) {
    console.error('Error fetching projects:', error)
    res.status(500).json({ error: 'Failed to fetch projects' })
  }
}

import type { VercelRequest, VercelResponse } from '@vercel/node'
import { put } from '@vercel/blob'
import formidable from 'formidable'
import { db } from '../../src/lib/db.js'
import { serviceInquirySchema } from '../../src/lib/validations/serviceInquirySchema.js'

// Disable body parsing - formidable will handle it
export const config = {
  api: {
    bodyParser: false,
  },
}

async function parseFormData(req: VercelRequest): Promise<{ fields: any; files: formidable.File[] }> {
  return new Promise((resolve, reject) => {
    const form = formidable({
      maxFileSize: 50 * 1024 * 1024, // 50MB
      maxFiles: 5,
      keepExtensions: true,
    })

    form.parse(req as any, (err, fields, files) => {
      if (err) {
        reject(err)
        return
      }

      // Convert fields from arrays to single values
      const parsedFields: any = {}
      for (const [key, value] of Object.entries(fields)) {
        parsedFields[key] = Array.isArray(value) ? value[0] : value
      }

      // Convert files to array
      const fileArray: formidable.File[] = []
      if (files.files) {
        const fileList = Array.isArray(files.files) ? files.files : [files.files]
        fileArray.push(...fileList)
      }

      resolve({ fields: parsedFields, files: fileArray })
    })
  })
}

async function sendToSlack(data: any): Promise<boolean> {
  const webhookUrl = process.env.SLACK_WEBHOOK_URL

  if (!webhookUrl) {
    console.warn('SLACK_WEBHOOK_URL not configured')
    return false
  }

  const slackMessage = {
    text: `New Service Inquiry: ${data.projectTitle}`,
    blocks: [
      {
        type: 'header',
        text: {
          type: 'plain_text',
          text: 'ðŸš€ New Service Inquiry'
        }
      },
      {
        type: 'section',
        fields: [
          {
            type: 'mrkdwn',
            text: `*Client:*\n${data.name} (${data.clientType})`
          },
          {
            type: 'mrkdwn',
            text: `*Email/Phone:*\n${data.email}\n${data.phone || 'N/A'}`
          },
          {
            type: 'mrkdwn',
            text: `*Project Title:*\n${data.projectTitle}`
          },
          {
            type: 'mrkdwn',
            text: `*Budget:*\nâ‚¹${data.budgetMin} - â‚¹${data.budgetMax}`
          }
        ]
      },
      {
        type: 'section',
        fields: [
          {
            type: 'mrkdwn',
            text: `*Deadline:*\n${data.deadline ? new Date(data.deadline).toLocaleDateString() : 'Flexible'}`
          },
          {
            type: 'mrkdwn',
            text: `*Domain:*\n${data.domain || 'Not specified'}`
          }
        ]
      },
      {
        type: 'section',
        text: {
          type: 'mrkdwn',
          text: `*Details:*\n${data.projectDetails.substring(0, 500)}${data.projectDetails.length > 500 ? '...' : ''}`
        }
      }
    ]
  }

  // Add file attachment notice if files exist
  if (data.fileUrls && data.fileUrls.length > 0) {
    const fileLinks = data.fileUrls.map((url: string, i: number) => `<${url}|File ${i + 1}>`).join(', ')
      ; (slackMessage.blocks as any[]).push({
        type: 'context',
        elements: [
          {
            type: 'mrkdwn',
            text: `ðŸ“Ž Attachments: ${fileLinks}`
          }
        ]
      })
  }

  // Add timestamp
  ; (slackMessage.blocks as any[]).push({
    type: 'context',
    elements: [
      {
        type: 'mrkdwn',
        text: `Submitted at: ${new Date().toLocaleString()}`
      }
    ]
  })

  try {
    const response = await fetch(webhookUrl, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(slackMessage),
    })

    if (!response.ok) {
      console.error('Slack webhook failed:', response.status)
      return false
    }

    console.log('âœ… Slack notification sent')
    return true
  } catch (error) {
    console.error('Error sending to Slack:', error)
    return false
  }
}

export default async function handler(req: VercelRequest, res: VercelResponse) {
  // Set CORS headers
  res.setHeader('Access-Control-Allow-Origin', '*')
  res.setHeader('Access-Control-Allow-Methods', 'POST, OPTIONS')
  res.setHeader('Access-Control-Allow-Headers', 'Content-Type')

  // Handle preflight requests
  if (req.method === 'OPTIONS') {
    return res.status(200).end()
  }

  // Only allow POST requests
  if (req.method !== 'POST') {
    return res.status(405).json({ error: 'Method not allowed' })
  }

  try {
    // Check if database is available
    if (!db) {
      console.error('âŒ Database connection not available')
      return res.status(503).json({
        error: 'Database connection not available. Please check DATABASE_URL environment variable.'
      })
    }

    // Parse FormData using formidable
    const { fields: formData, files } = await parseFormData(req)

    // Extract form fields (keep raw string values for validation)
    const inquiryData = {
      name: formData.name,
      email: formData.email,
      phone: formData.phone ?? undefined,
      clientType: formData.clientType ?? undefined,
      studyYear: formData.studyYear ?? undefined,
      projectTitle: formData.projectTitle,
      domain: formData.domain ?? undefined,
      projectDetails: formData.projectDetails,
      dataset: formData.dataset ?? undefined,
      budgetMin: formData.budgetMin ?? undefined,
      budgetMax: formData.budgetMax ?? undefined,
      deadline: formData.deadline ?? undefined,
    }

    // Validate form data
    const validatedData = serviceInquirySchema.parse(inquiryData)

    // Create the inquiry in database first
    const inquiry = await db.serviceInquiry.create({
      data: {
        name: validatedData.name,
        email: validatedData.email,
        phone: validatedData.phone,
        clientType: validatedData.clientType,
        studyYear: validatedData.studyYear,
        projectTitle: validatedData.projectTitle,
        domain: validatedData.domain || null,
        projectDetails: validatedData.projectDetails,
        dataset: validatedData.dataset || null,
        budgetMin: validatedData.budgetMin ? Number(validatedData.budgetMin) : null,
        budgetMax: validatedData.budgetMax ? Number(validatedData.budgetMax) : null,
        deadline: validatedData.deadline ? new Date(validatedData.deadline) : null,
        fileUrl: null, // Will update after file upload
      },
    })

    // Handle file uploads if present
    let fileUrls: string[] = []

    if (files && files.length > 0) {
      if (!process.env.BLOB_READ_WRITE_TOKEN) {
        console.warn('âš ï¸ BLOB_READ_WRITE_TOKEN not set, skipping file upload')
      } else {
        try {
          const sanitizedName = validatedData.name.replace(/[^a-zA-Z0-9]/g, '_')

          for (const file of files) {
            // Read file from filesystem (formidable saves to disk by default)
            const fs = await import('fs')
            const fileBuffer = fs.readFileSync(file.filepath)
            const fileName = file.originalFilename || file.newFilename || `file_${Date.now()}`
            const filePath = `service-inquiries/${sanitizedName}/${fileName}`

            const { url } = await put(filePath, fileBuffer, {
              access: 'public',
              contentType: file.mimetype || 'application/octet-stream',
            })

            fileUrls.push(url)

            // Clean up temporary file
            fs.unlinkSync(file.filepath)
          }

          // Update the inquiry with file URLs
          if (fileUrls.length > 0) {
            await db.serviceInquiry.update({
              where: { id: inquiry.id },
              data: {
                fileUrl: fileUrls.join('\n'),
              },
            })
          }

          console.log(`âœ… Uploaded ${fileUrls.length} file(s) to Vercel Blob`)
        } catch (fileError) {
          console.error('âŒ Error uploading files:', fileError)
          // Continue even if file upload fails - inquiry is already saved
        }
      }
    }

    // Send notification to Slack
    await sendToSlack({ ...validatedData, fileUrls })

    console.log(`âœ… Service inquiry saved: ${inquiry.id}`)
    console.log(`   Name: ${validatedData.name}`)
    console.log(`   Email: ${validatedData.email}`)
    console.log(`   Project: ${validatedData.projectTitle}`)

    return res.status(200).json({
      success: true,
      message: 'Inquiry submitted successfully! I\'ll get back to you within 24 hours.',
      inquiryId: inquiry.id
    })

  } catch (error: any) {
    console.error('âŒ Failed to process service inquiry:', error)

    // Handle validation errors
    if (error.name === 'ZodError' || error.issues) {
      const zodError = error.issues ? error : { issues: error.errors }
      return res.status(400).json({
        error: 'Validation failed',
        details: zodError.issues.map((e: any) => `${e.path.join('.')}: ${e.message}`)
      })
    }

    return res.status(500).json({
      error: 'Failed to process service inquiry. Please try again or contact me directly.'
    })
  }
}

import { VercelRequest, VercelResponse } from '@vercel/node'

export default async function handler(req: VercelRequest, res: VercelResponse) {
  // Set CORS headers
  res.setHeader('Access-Control-Allow-Origin', '*')
  res.setHeader('Access-Control-Allow-Methods', 'GET, OPTIONS')
  res.setHeader('Access-Control-Allow-Headers', 'Content-Type')

  // Handle preflight requests
  if (req.method === 'OPTIONS') {
    return res.status(200).end()
  }

  // Only allow GET requests
  if (req.method !== 'GET') {
    return res.status(405).json({ error: 'Method not allowed' })
  }

  try {
    const { platform, username } = req.query

    if (!platform || !username || typeof platform !== 'string' || typeof username !== 'string') {
      return res.status(400).json({ error: 'Platform and username are required' })
    }

    let result: any = {}

    switch (platform.toLowerCase()) {
      case 'github':
        result = await fetchGitHubStats(username)
        break
      // case 'leetcode':
      //   result = await fetchLeetCodeStats(username)
      //   break
      case 'codechef':
        result = await fetchCodeChefStats(username)
        break
      case 'twitter':
        result = await fetchTwitterStats(username)
        break
      default:
        return res.status(400).json({ error: 'Unsupported platform' })
    }

    res.status(200).json(result)
  } catch (error) {
    console.error(`Social stats API error:`, error)

    res.status(500).json({
      error: error instanceof Error ? error.message : 'Unknown error',
      isLoading: false
    })
  }
}

async function fetchGitHubStats(username: string) {
  const [userResponse, reposResponse] = await Promise.all([
    fetch(`https://api.github.com/users/${username}`, {
      headers: {
        'Accept': 'application/vnd.github.v3+json',
        'User-Agent': 'himanshu-portfolio-website',
        ...(process.env.GITHUB_TOKEN ? { 'Authorization': `token ${process.env.GITHUB_TOKEN}` } : {})
      }
    }),
    fetch(`https://api.github.com/users/${username}/repos?per_page=100&sort=updated`, {
      headers: {
        'Accept': 'application/vnd.github.v3+json',
        'User-Agent': 'himanshu-portfolio-website',
        ...(process.env.GITHUB_TOKEN ? { 'Authorization': `token ${process.env.GITHUB_TOKEN}` } : {})
      }
    })
  ])

  if (!userResponse.ok || !reposResponse.ok) {
    throw new Error(`GitHub API error: User ${userResponse.status}, Repos ${reposResponse.status}`)
  }

  const userData = await userResponse.json()
  const reposData = await reposResponse.json()

  const totalStars = reposData.reduce((acc: number, repo: any) => acc + repo.stargazers_count, 0)
  const totalForks = reposData.reduce((acc: number, repo: any) => acc + repo.forks_count, 0)
  const totalWatchers = reposData.reduce((acc: number, repo: any) => acc + repo.watchers_count, 0)

  return {
    stars: totalStars,
    forks: totalForks,
    watchers: totalWatchers,
    repositories: userData.public_repos,
    followers: userData.followers,
    following: userData.following,
    isLoading: false,
    error: null
  }
}

