// Helper function to filter out conflicting props between HTML and Framer Motion
const filterMotionProps = (props: any) => {
  const { 
    onDrag, 
    onDragStart, 
    onDragEnd,
    onCopy,
    onCopyCapture,
    onCut,
    onCutCapture,
    onPaste,
    onPasteCapture,
    ...filteredProps 
  } = props
  return filteredProps
}

