import React, { createContext, useContext, useEffect, useState } from 'react'

type Theme = 'light' | 'dark'

interface ThemeContextType {
  theme: Theme
  toggleTheme: () => void
  setTheme: (theme: Theme) => void
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined)

export const useTheme = () => {
  const context = useContext(ThemeContext)
  if (context === undefined) {
    throw new Error('useTheme must be used within a ThemeProvider')
  }
  return context
}

interface ThemeProviderProps {
  children: React.ReactNode
}

export const ThemeProvider: React.FC<ThemeProviderProps> = ({ children }) => {
  const [theme, setThemeState] = useState<Theme>(() => {
    // Check if we're in a test environment
    if (typeof window === 'undefined' || !window.matchMedia) {
      return 'light'
    }
    
    // Check if theme is already applied by the initialization script
    const htmlElement = document.documentElement
    if (htmlElement.classList.contains('dark')) {
      return 'dark'
    } else if (htmlElement.classList.contains('light')) {
      return 'light'
    }
    
    // Check localStorage first
    const savedTheme = localStorage.getItem('theme') as Theme
    if (savedTheme) {
      return savedTheme
    }
    
    // Check system preference
    if (window.matchMedia('(prefers-color-scheme: dark)').matches) {
      return 'dark'
    }
    
    return 'light'
  })

  useEffect(() => {
    const root = document.documentElement
    
    // Remove previous theme class
    root.classList.remove('light', 'dark')
    
    // Add current theme class
    root.classList.add(theme)
    
    // Save to localStorage
    localStorage.setItem('theme', theme)
  }, [theme])

  const toggleTheme = () => {
    setThemeState(prevTheme => prevTheme === 'light' ? 'dark' : 'light')
  }

  const setTheme = (newTheme: Theme) => {
    setThemeState(newTheme)
  }

  const value: ThemeContextType = {
    theme,
    toggleTheme,
    setTheme,
  }

  return (
    <ThemeContext.Provider value={value}>
      {children}
    </ThemeContext.Provider>
  )
}

import { useState, useEffect } from 'react'

export const useAnalytics = () => {
  const [isAnalyticsEnabled, setIsAnalyticsEnabled] = useState(false)
  const [hasConsent, setHasConsent] = useState<boolean | null>(null)

  useEffect(() => {
    // Check if user has given consent
    const consent = localStorage.getItem('analytics-consent')
    if (consent === 'accepted') {
      setIsAnalyticsEnabled(true)
      setHasConsent(true)
    } else if (consent === 'declined') {
      setIsAnalyticsEnabled(false)
      setHasConsent(false)
    } else {
      setHasConsent(null) // No decision made yet
    }
  }, [])

  const enableAnalytics = () => {
    setIsAnalyticsEnabled(true)
    localStorage.setItem('analytics-consent', 'accepted')
  }

  const disableAnalytics = () => {
    setIsAnalyticsEnabled(false)
    localStorage.setItem('analytics-consent', 'declined')
  }

  const trackEvent = (eventName: string, properties?: Record<string, any>) => {
    if (isAnalyticsEnabled && typeof window !== 'undefined') {
      // Vercel Analytics tracking
      if (window.va) {
        window.va.track(eventName, properties)
      }
    }
  }

  const trackPageView = (url: string) => {
    if (isAnalyticsEnabled && typeof window !== 'undefined') {
      // Vercel Analytics page view tracking
      if (window.va) {
        window.va.track('page_view', { url })
      }
    }
  }

  return {
    isAnalyticsEnabled,
    hasConsent,
    enableAnalytics,
    disableAnalytics,
    trackEvent,
    trackPageView,
  }
}

// Extend Window interface for Vercel Analytics
declare global {
  interface Window {
    va?: {
      track: (eventName: string, properties?: Record<string, any>) => void
    }
  }
}

import { useState, useEffect } from 'react'

interface ArticleLike {
  articleId: string
  likes: number
  isLiked: boolean
}

interface ArticleView {
  articleId: string
  views: number
  lastViewed: number
}

interface ArticleStatistics {
  totalLikes: number
  totalViews: number
  getArticleLikes: (articleId: string) => number
  getArticleViews: (articleId: string) => number
}

const LIKES_STORAGE_KEY = 'article-likes'
const VIEWS_STORAGE_KEY = 'article-views'

export const useArticleStatistics = (articles: any[]): ArticleStatistics => {
  const [totalLikes, setTotalLikes] = useState(0)
  const [totalViews, setTotalViews] = useState(0)

  // Function to recalculate statistics
  const recalculateStatistics = () => {
    // Calculate total likes from localStorage
    const storedLikes = localStorage.getItem(LIKES_STORAGE_KEY)
    let parsedLikes: ArticleLike[] = []
    
    if (storedLikes) {
      try {
        parsedLikes = JSON.parse(storedLikes)
      } catch (error) {
        console.error('Error parsing stored likes:', error)
      }
    }

    // Calculate total views from localStorage
    const storedViews = localStorage.getItem(VIEWS_STORAGE_KEY)
    let parsedViews: ArticleView[] = []
    
    if (storedViews) {
      try {
        parsedViews = JSON.parse(storedViews)
      } catch (error) {
        console.error('Error parsing stored views:', error)
      }
    }

    // Calculate totals
    const likesTotal = articles.reduce((sum, article) => {
      const storedLike = parsedLikes.find(like => like.articleId === article.id)
      return sum + (storedLike ? storedLike.likes : article.likes)
    }, 0)

    const viewsTotal = articles.reduce((sum, article) => {
      const storedView = parsedViews.find(view => view.articleId === article.id)
      return sum + (storedView ? storedView.views : article.views)
    }, 0)

    setTotalLikes(likesTotal)
    setTotalViews(viewsTotal)
  }

  useEffect(() => {
    recalculateStatistics()
  }, [articles])

  // Listen for localStorage changes to recalculate statistics
  useEffect(() => {
    const handleStorageChange = () => {
      recalculateStatistics()
    }

    window.addEventListener('storage', handleStorageChange)
    
    // Also listen for custom events (for same-tab changes)
    window.addEventListener('articleDataChanged', handleStorageChange)

    return () => {
      window.removeEventListener('storage', handleStorageChange)
      window.removeEventListener('articleDataChanged', handleStorageChange)
    }
  }, [articles])

  const getArticleLikes = (articleId: string): number => {
    const storedLikes = localStorage.getItem(LIKES_STORAGE_KEY)
    if (storedLikes) {
      try {
        const parsedLikes: ArticleLike[] = JSON.parse(storedLikes)
        const articleLike = parsedLikes.find(like => like.articleId === articleId)
        if (articleLike) {
          return articleLike.likes
        }
      } catch (error) {
        console.error('Error parsing stored likes:', error)
      }
    }
    
    // Fallback to article data
    const article = articles.find(a => a.id === articleId)
    return article ? article.likes : 0
  }

  const getArticleViews = (articleId: string): number => {
    const storedViews = localStorage.getItem(VIEWS_STORAGE_KEY)
    if (storedViews) {
      try {
        const parsedViews: ArticleView[] = JSON.parse(storedViews)
        const articleView = parsedViews.find(view => view.articleId === articleId)
        if (articleView) {
          return articleView.views
        }
      } catch (error) {
        console.error('Error parsing stored views:', error)
      }
    }
    
    // Fallback to article data
    const article = articles.find(a => a.id === articleId)
    return article ? article.views : 0
  }

  return {
    totalLikes,
    totalViews,
    getArticleLikes,
    getArticleViews
  }
}

import { useEffect, useCallback, useRef, useState } from 'react'

// Throttle function for scroll events
export const throttle = <T extends (...args: any[]) => any>(
  func: T,
  limit: number
): ((...args: Parameters<T>) => void) => {
  let inThrottle: boolean
  return function (this: any, ...args: Parameters<T>) {
    if (!inThrottle) {
      func.apply(this, args)
      inThrottle = true
      setTimeout(() => (inThrottle = false), limit)
    }
  }
}

// Debounce function for scroll events
export const debounce = <T extends (...args: any[]) => any>(
  func: T,
  delay: number
): ((...args: Parameters<T>) => void) => {
  let timeoutId: NodeJS.Timeout
  return function (this: any, ...args: Parameters<T>) {
    clearTimeout(timeoutId)
    timeoutId = setTimeout(() => func.apply(this, args), delay)
  }
}

// Optimized scroll hook
export const useScrollOptimization = () => {
  const scrollY = useRef(0)
  const ticking = useRef(false)

  const updateScrollPosition = useCallback(() => {
    scrollY.current = window.scrollY
    ticking.current = false
  }, [])

  const requestTick = useCallback(() => {
    if (!ticking.current) {
      requestAnimationFrame(updateScrollPosition)
      ticking.current = true
    }
  }, [updateScrollPosition])

  useEffect(() => {
    const handleScroll = throttle(requestTick, 16) // ~60fps

    window.addEventListener('scroll', handleScroll, { passive: true })
    return () => window.removeEventListener('scroll', handleScroll)
  }, [requestTick])

  return scrollY.current
}

// Intersection Observer hook for scroll-triggered animations
export const useIntersectionObserver = (
  callback: (entries: IntersectionObserverEntry[]) => void,
  options: IntersectionObserverInit = {}
) => {
  const observerRef = useRef<IntersectionObserver | null>(null)

  useEffect(() => {
    observerRef.current = new IntersectionObserver(callback, {
      threshold: 0.1,
      rootMargin: '50px',
      ...options
    })

    return () => {
      if (observerRef.current) {
        observerRef.current.disconnect()
      }
    }
  }, [callback, options])

  return observerRef.current
}

// Performance monitoring for scroll
export const useScrollPerformance = () => {
  const frameCount = useRef(0)
  const lastTime = useRef(performance.now())
  const fps = useRef(60)

  useEffect(() => {
    const measureFPS = () => {
      frameCount.current++
      const currentTime = performance.now()
      
      if (currentTime - lastTime.current >= 1000) {
        fps.current = Math.round((frameCount.current * 1000) / (currentTime - lastTime.current))
        frameCount.current = 0
        lastTime.current = currentTime
        
        // Log performance warnings
        if (fps.current < 30) {
          console.warn(`Low scroll FPS detected: ${fps.current}fps`)
        }
      }
      
      requestAnimationFrame(measureFPS)
    }

    measureFPS()
  }, [])

  return fps.current
}

// Optimized scroll-based animations
export const useScrollAnimation = (
  elementRef: React.RefObject<HTMLElement>,
  animationConfig: {
    trigger?: number
    duration?: number
    easing?: string
  } = {}
) => {
  const { trigger = 0.5, duration = 0.6, easing = 'ease-out' } = animationConfig
  const [isVisible, setIsVisible] = useState(false)
  const [scrollProgress, setScrollProgress] = useState(0)

  useEffect(() => {
    const element = elementRef.current
    if (!element) return

    const observer = new IntersectionObserver(
      ([entry]) => {
        const progress = entry.intersectionRatio
        setScrollProgress(progress)
        setIsVisible(progress > trigger)
      },
      {
        threshold: Array.from({ length: 101 }, (_, i) => i / 100),
        rootMargin: '0px'
      }
    )

    observer.observe(element)
    return () => observer.disconnect()
  }, [elementRef, trigger])

  return { isVisible, scrollProgress }
}

// Reduce motion preference
export const useReducedMotion = () => {
  const [prefersReducedMotion, setPrefersReducedMotion] = useState(false)

  useEffect(() => {
    const mediaQuery = window.matchMedia('(prefers-reduced-motion: reduce)')
    setPrefersReducedMotion(mediaQuery.matches)

    const handleChange = (e: MediaQueryListEvent) => {
      setPrefersReducedMotion(e.matches)
    }

    mediaQuery.addEventListener('change', handleChange)
    return () => mediaQuery.removeEventListener('change', handleChange)
  }, [])

  return prefersReducedMotion
}

// Scroll performance utilities
export const scrollUtils = {
  // Check if element is in viewport
  isInViewport: (element: HTMLElement, threshold: number = 0) => {
    const rect = element.getBoundingClientRect()
    return (
      rect.top >= -threshold &&
      rect.left >= -threshold &&
      rect.bottom <= (window.innerHeight || document.documentElement.clientHeight) + threshold &&
      rect.right <= (window.innerWidth || document.documentElement.clientWidth) + threshold
    )
  },

  // Get scroll progress (0-1)
  getScrollProgress: () => {
    const scrollTop = window.pageYOffset || document.documentElement.scrollTop
    const scrollHeight = document.documentElement.scrollHeight - window.innerHeight
    return Math.min(scrollTop / scrollHeight, 1)
  },

  // Smooth scroll to element
  smoothScrollTo: (element: HTMLElement, offset: number = 0) => {
    const elementPosition = element.getBoundingClientRect().top + window.pageYOffset
    const offsetPosition = elementPosition - offset

    window.scrollTo({
      top: offsetPosition,
      behavior: 'smooth'
    })
  },

  // Pause animations during scroll
  pauseAnimations: () => {
    document.body.style.setProperty('--animation-play-state', 'paused')
  },

  // Resume animations after scroll
  resumeAnimations: () => {
    document.body.style.setProperty('--animation-play-state', 'running')
  }
}

