import { useState, useEffect } from 'react'
import { useProfileStats } from './useProfileStats'
import { useSocialStats } from './useSocialStats'
import { useArticleStatistics } from './useArticleStatistics'
import { articles } from '../data/articles'

interface UnifiedStats {
  // Profile Stats
  projectsCompleted: number
  yearsExperience: number
  technologiesMastered: number
  
  // Social Stats
  githubStars: number
  githubRepositories: number
  githubFollowers: number
  linkedinFollowers: number
  
  // Article Stats
  totalArticles: number
  
  // Loading States
  isLoading: boolean
  error: string | null
}

export const useUnifiedStats = (): UnifiedStats => {
  const [stats, setStats] = useState<UnifiedStats>({
    projectsCompleted: 0,
    yearsExperience: 0,
    technologiesMastered: 40, // Static count from your tech stack
    githubStars: 0,
    githubRepositories: 0,
    githubFollowers: 0,
    linkedinFollowers: 0,
    totalArticles: 0,
    isLoading: true,
    error: null
  })

  const profileStats = useProfileStats()
  const socialStats = useSocialStats()

  useEffect(() => {
    setStats({
      // Profile Stats
      projectsCompleted: profileStats.projectsCompleted,
      yearsExperience: profileStats.yearsExperience,
      technologiesMastered: 40, // From your tech stack count
      
      // Social Stats
      githubStars: socialStats.github.stars || 0,
      githubRepositories: socialStats.github.repositories || 0,
      githubFollowers: socialStats.github.followers || 0,
      linkedinFollowers: socialStats.linkedin.followers || 0,
      
      // Article Stats
      totalArticles: articles.length,
      
      // Loading States
      isLoading: profileStats.isLoading || socialStats.github.isLoading,
      error: socialStats.github.error
    })
  }, [profileStats, socialStats])

  return stats
}

// Client-side project API utilities
import { type Project } from './clientMdx'

// API base URL - use relative path for production, localhost for development
const API_BASE_URL = import.meta.env.DEV ? 'http://localhost:5000/api' : '/api'

// Generic fetch function with error handling
async function apiFetch<T>(endpoint: string): Promise<T> {
  try {
    // Add cache-busting query parameter to ensure fresh data
    const cacheBuster = `?t=${Date.now()}`
    const url = `${API_BASE_URL}${endpoint}${endpoint.includes('?') ? '&' : '?'}t=${Date.now()}`

    const response = await fetch(url, {
      cache: 'no-store', // Disable browser cache
      headers: {
        'Cache-Control': 'no-cache',
        'Pragma': 'no-cache'
      }
    })

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`)
    }

    return await response.json()
  } catch (error) {
    console.error(`API fetch error for ${endpoint}:`, error)
    throw error
  }
}

// Fetch all projects
export async function fetchAllProjects(): Promise<Project[]> {
  return apiFetch<Project[]>('/projects')
}

// Fetch project by slug
export async function fetchProject(slug: string): Promise<Project | null> {
  try {
    return await apiFetch<Project>(`/projects/${slug}`)
  } catch (error) {
    console.error(`Error fetching project ${slug}:`, error)
    return null
  }
}

// Fetch featured projects
export async function fetchFeaturedProjects(): Promise<Project[]> {
  return apiFetch<Project[]>('/projects/featured')
}

// Fetch project navigation
export async function fetchProjectNavigation(slug: string): Promise<{ previous: Project | null; next: Project | null }> {
  return apiFetch<{ previous: Project | null; next: Project | null }>(`/projects/${slug}/navigation`)
}

// Fetch related projects
export async function fetchRelatedProjects(slug: string, limit: number = 3): Promise<Project[]> {
  return apiFetch<Project[]>(`/projects/${slug}/related?limit=${limit}`)
}

// Generate project metadata for SEO
export function generateProjectMetadata(project: any) {
  return {
    title: `${project.title} - Portfolio`,
    description: project.summary,
    keywords: project.techStack.join(', '),
    openGraph: {
      title: `${project.title} - Portfolio`,
      description: project.summary,
      image: `${process.env.NODE_ENV === 'production' ? 'https://himanshu-salunke.vercel.app' : 'http://localhost:3000'}/api/og/${project.slug}?title=${encodeURIComponent(project.title)}&description=${encodeURIComponent(project.summary)}&type=project&date=${project.date}&tags=${encodeURIComponent(project.techStack.join(','))}`,
      type: 'article',
      publishedTime: project.date,
      authors: ['Himanshu'],
      tags: project.techStack
    },
    twitter: {
      card: 'summary_large_image',
      title: `${project.title} - Portfolio`,
      description: project.summary,
      image: `${process.env.NODE_ENV === 'production' ? 'https://himanshu-salunke.vercel.app' : 'http://localhost:3000'}/api/og/${project.slug}?title=${encodeURIComponent(project.title)}&description=${encodeURIComponent(project.summary)}&type=project&date=${project.date}&tags=${encodeURIComponent(project.techStack.join(','))}`
    }
  }
}

import { readFileSync, writeFileSync } from 'fs';
import { join } from 'path';

interface SitemapEntry {
  url: string;
  lastmod: string;
  changefreq: 'always' | 'hourly' | 'daily' | 'weekly' | 'monthly' | 'yearly' | 'never';
  priority: number;
}

export class SitemapGenerator {
  private baseUrl: string;
  private entries: SitemapEntry[] = [];

  constructor(baseUrl: string) {
    this.baseUrl = baseUrl;
  }

  addEntry(url: string, lastmod?: string, changefreq: SitemapEntry['changefreq'] = 'weekly', priority: number = 0.8) {
    this.entries.push({
      url: `${this.baseUrl}${url}`,
      lastmod: lastmod || new Date().toISOString().split('T')[0],
      changefreq,
      priority
    });
  }

  generateSitemap(): string {
    const header = `<?xml version="1.0" encoding="UTF-8"?>
<urlset xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">`;

    const entries = this.entries.map(entry => `  <url>
    <loc>${entry.url}</loc>
    <lastmod>${entry.lastmod}</lastmod>
    <changefreq>${entry.changefreq}</changefreq>
    <priority>${entry.priority}</priority>
  </url>`).join('\n');

    const footer = `</urlset>`;

    return `${header}\n${entries}\n${footer}`;
  }

  async generateFromContent() {
    // Add main pages
    this.addEntry('/', undefined, 'daily', 1.0);
    this.addEntry('/about', undefined, 'monthly', 0.9);
    this.addEntry('/work', undefined, 'weekly', 0.9);
    this.addEntry('/contact', undefined, 'monthly', 0.8);

    // Add projects
    try {
      const projectsPath = join(process.cwd(), 'src/data/projects');
      const projectFiles = require('fs').readdirSync(projectsPath).filter((file: string) => file.endsWith('.mdx'));
      
      projectFiles.forEach((file: string) => {
        const slug = file.replace('.mdx', '');
        this.addEntry(`/work/${slug}`, undefined, 'monthly', 0.8);
      });
    } catch (error) {
      console.warn('Could not read projects directory:', error);
    }

    return this.generateSitemap();
  }

  saveSitemap(outputPath: string) {
    const sitemap = this.generateSitemap();
    writeFileSync(outputPath, sitemap, 'utf8');
    console.log(`Sitemap generated at: ${outputPath}`);
  }
}
